import React from 'react';
import { VStack, FormControl, Input, NativeBaseProvider, Center, Button } from 'native-base';

export default function FormExample() {
    const [formData, setData] = React.useState({});
    const [errors, setErrors] = React.useState({});

    const validate = () => {
        if (formData.name === undefined) {
        setErrors({ ...errors,
        name: 'Name is required'
        });
        return false;
    } else if (formData.name.length < 3) {
        setErrors({ ...errors,
        name: 'Name is too short'
        });
        return false;
    }

    return true;
    };

    const onSubmit = () => {
    validate() ? console.log('Submitted') : console.log('Validation Failed');
    };

    return <VStack width="90%" mx="3" maxW="300px">
        <FormControl isRequired isInvalid={'name' in errors}>
        <FormControl.Label _text={{
        bold: true
        }}>Name</FormControl.Label>
        <Input placeholder="John" onChangeText={value => setData({ ...formData,
        name: value
        })} />
        {'name' in errors ? <FormControl.ErrorMessage>{errors.name}</FormControl.ErrorMessage> : <FormControl.HelperText>
            Name should contain atleast 3 character.
            </FormControl.HelperText>}
        </FormControl>
        <Button onPress={onSubmit} mt="5" colorScheme="cyan">
        Submit
        </Button>
    </VStack>;
    }
