import React from 'react';
import { VStack, FormControl, Input, NativeBaseProvider, Center, Button, Image } from 'native-base';

export default function FormExample() {
    const [formData, setData] = React.useState({});
    const [errors, setErrors] = React.useState({});
    const [isPassValid, setisPassValid] = React.useState(false);

    // RegEx
    const passRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[@#$%^&+!=])[A-Za-z0-9@#$%^&+!=]{8,}$/;

    const validateName = () => {
        if (formData.name === undefined) {
        setErrors({ ...errors,
        name: 'Name is required'
        });
        return false;
    } else if (formData.name.length < 3) {
        setErrors({ ...errors,
        name: 'Name is too short'
        });
        return false;
    }

    return true;
    };

    const onSubmit = () => {
    validateName() ? console.log('Submitted') : console.log('Validation Failed');
    };

    function onSetPassword(value) {
        setPassword(value);
        setisPassValid(passRegex.test(value));
        
    }

    const validatePassword = () => {
        if (formData.password === undefined) {
        setErrors({ ...errors,
        password: 'Pasword is required'
        });
        return false;
    } else if (!isPassValid) {
        setErrors({ ...errors,
        password: 'Password must have 1 lowercase, capita letter, number, special character, and lenght of >8'
        });
        return false;
    }

    // Form
    return <VStack width="90%" mx="3" maxW="300px">
        <Image
        source={require("../assets/logo.png")}
        alt="Alternate Text"
        size="xl"
        marginLeft={35}
        />
        {/* Name */}
        <FormControl isRequired isInvalid={'name' in errors}>
        <FormControl.Label _text={{
        bold: true
        }}>Name</FormControl.Label>
        <Input placeholder="John" onChangeText={value => setData({ ...formData,
        name: value
        })} />
        {'name' in errors ? <FormControl.ErrorMessage>{errors.name}</FormControl.ErrorMessage> : <FormControl.HelperText>
            Name should contain atleast 3 character.
            </FormControl.HelperText>}
        </FormControl>
         {/* Password */}
        <FormControl isRequired isInvalid={'password' in errors}>
        <FormControl.Label _text={{ bold: true }}>Password</FormControl.Label>
        <Input
        placeholder="Password"
        onChangeText={(value) => {
            setData({ ...formData, password: value });
            onSetPassword(value); // update isPassValid
        }}
        secureTextEntry // hide password
        />
        {'password' in errors ? (
        <FormControl.ErrorMessage>{errors.password}</FormControl.ErrorMessage>
        ) : (
        <FormControl.HelperText>
            Password must have 1 lowercase, capital letter, number, special character, and a length of 8 or more.
        </FormControl.HelperText>
        )}
        </FormControl>
         {/* Submit button */}
        <Button onPress={onSubmit} mt="5" colorScheme="cyan">
        Submit
        </Button>
    </VStack>;
    }
}
